import React, { useState } from 'react';

import './ExpenseForm.css';

const ExpenseForm = () => {
	// const [enteredTitle, setEnteredTitle] = useState('');
	// const [enteredAmount, setEnteredAmount] = useState('');
	// const [enteredDate, setEnteredDate] = useState('');
	const [userInput, setUserInput] = useState({
		enteredTitle: '',
		enteredAmount: '',
		enteredDate: '',
	});

	const titleChangeHandler = ({ target }) =>
		userInput({ ...userInput, enteredTitle: target.value });
	const amountChangeHandler = ({ target }) =>
		userInput({ ...userInput, enteredAmount: target.value });
	const dateChangeHandler = ({ target }) =>
		userInput({ ...userInput, enteredDate: target.value });

	const onSubmitHandler = () => {
		console.log();
	};

	return (
		<form onSubmit={onSubmitHandler}>
			<div className='new-expense__controls'>
				<div className='new-expense__control'>
					<label htmlFor='title'>Title</label>
					<input
						onChange={titleChangeHandler}
						type='text'
						name='title'
						id='title'
					/>
				</div>
				<div className='new-expense__control'>
					<label htmlFor='amount'>Amount</label>
					<input
						onChange={amountChangeHandler}
						type='number'
						min='1'
						step='1'
						name='amount'
						id='amount'
					/>
				</div>
				<div className='new-expense__control'>
					<label htmlFor='date'>Date</label>
					<input
						onChange={dateChangeHandler}
						type='date'
						name='date'
						id='date'
					/>
				</div>
			</div>
			<button className='new-expense__actions' type='submit'>
				Add Expense
			</button>
		</form>
	);
};

export default ExpenseForm;
