	const day = date.toLocaleString('en-US', { day: '2-digit' });
	const year = date.getFullYear();

	return (
		<div className='expense-item' key={id}>
			<div>{ExpenseDate}</div>
			<div className='expense-item__description'>
				<h2>{title}</h2>
				<div className='expense-item__price'>${amount}</div>
			</div>
		</div>
	);
}
